export const newBrokerPageTemplate = (context, definition) => html`
  <template>
    <${'ppp-page-header'}>Новый профиль брокера</ppp-page-header>
    ${when((x) => !x.broker, brokerSelectionTemplate)}
    <form ${ref(
      'form'
    )} id="new-broker" name="new-broker" onsubmit="return false">
      <div class="loading-wrapper" ?busy="${(x) => !!x.busy}">
        ${when(
          (x) => x.broker === SUPPORTED_BROKERS.ALOR_OPENAPI_V2,
          alorOpenAPIV2Template
        )}
        ${when(
          (x) => x.broker === SUPPORTED_BROKERS.UNITED_TRADERS,
          unitedTradersTemplate
        )}
        ${when(
          (x) => x.broker === SUPPORTED_BROKERS.TINKOFF_OPENAPI_V1,
          tinkoffOpenAPIV1Template
        )}
        ${when((x) => !!x.busy, html`${loadingIndicator()}`)}
      </div>
      ${when(
        (x) => x.broker,
        html`
          <section class="last">
            <div class="footer-actions">
              <${'ppp-button'}
                ?disabled="${(x) => !!x.busy}"
                type="submit"
                @click="${(x) => x.createBroker()}"
                appearance="primary"
              >
                ${when(
                  (x) => !!x.busy,
                  settings({
                    slot: 'end',
                    cls: 'spinner-icon'
                  })
                )}
                ${i18n.t('save')}
              </ppp-button>
            </div>
          </section>`
      )}
    </form>
  </template>
`;

